userDefinedLinkHintCss:
div > .vimiumHintMarker {
/* linkhint boxes */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#c0c0c0),
  color-stop(100%,#5f5f5f));
border: 1px solid #505050;
}

div > .vimiumHintMarker span {
/* linkhint text */
color: #efefef;
font-weight: bold;
font-size: 12px;
}

div > .vimiumHintMarker > .matchingCharacter {
color: #808080;
}
